import R from 'ramda'
import {
    ADD_PHONE_TO_BASKET,
    REMOVEPHONEFROMBASKET,
    CLEAN_BASKET
} from 'actionTypes'

const initialeState = []

export default (state = initialeState, { type, payload }) => {
    switch (type) {
        case ADD_PHONE_TO_BASKET:
            return R.append(payload, state)
        case REMOVEPHONEFROMBASKET:
            return R.without(R.of(payload), state)
        case CLEAN_BASKET:
            return [];
        default:
            return state
    }
}